IF NOT EXISTS (SELECT name FROM sys.databases WHERE name = N'infolutions_agenda_cliente')
BEGIN
    CREATE DATABASE infolutions_agenda_cliente;
END
GO

USE infolutions_agenda_cliente;
GO

CREATE TABLE agenda_clientes (
    id INT IDENTITY(1,1) PRIMARY KEY,
    nombre VARCHAR(100),
    proyecto VARCHAR(200),
    empresa VARCHAR(100),
    telefono VARCHAR(20),
    correo VARCHAR(100),
    fecha_de_ingreso DATE,
    hora_de_agenda DATETIME,
    fecha_hora_atencion_oficina DATETIME NULL
);
